function profileClick(e){const t=document.querySelector(e);if(!t)return;t.onclick=()=>{location.href=location.origin+"/about"}}function menuOnScroll(){const e=document.getElementById("menu");e&&localStorage.setItem("menu-scroll-position",e.scrollLeft)}function restoreMenuScroll(){const e=document.getElementById("menu");if(e){const t=localStorage.getItem("menu-scroll-position");t!==null&&(e.scrollLeft=t)}}function updateActiveMenu(){const e=window.location.pathname.replace(/\/$/,"")+"/";document.querySelectorAll("#menu a").forEach(t=>{const n=new URL(t.href).pathname.replace(/\/$/,"")+"/";t.classList.toggle("active",e===n)})}const swup=new Swup;function initCustomFunction(){initTOC(".toc"),profileClick(".profile-img"),restoreMenuScroll(),updateActiveMenu()}document.addEventListener("DOMContentLoaded",()=>{initCustomFunction()}),swup.hooks.on("page:view",()=>{initCustomFunction()})